package com.codewithz;

public class Car {
	
	String name;
	String price;
	public Car() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Car(String name, String price) {
		super();
		this.name = name;
		this.price = price;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getPrice() {
		return price;
	}
	public void setPrice(String price) {
		this.price = price;
	}
	
	

}
